//000000086400a:2:{i:0;a:9:{s:2:"id";s:2:"50";s:5:"title";s:21:"早晨从中午开始";s:4:"pic1";s:0:"";s:4:"hits";s:2:"11";s:7:"content";s:390:"《早晨从中午开始》是路遥《平凡的世界》创作随笔。通篇写了他在《人生》获得巨大成功后，没有在荣耀面前沾沾自喜，而是自勉自励，相信自己能取得更大的成功，相信《人生》不是自己难以逾越的横杆，继而萌生了创作鸿篇巨著的伟大设想，那就是后来获得茅盾文学奖的《平凡的世界》。";s:7:"classid";s:1:"4";s:4:"time";s:10:"1422786215";s:3:"tag";s:15:"路遥，好书";s:9:"classname";s:12:"好书推荐";}i:1;a:9:{s:2:"id";s:2:"12";s:5:"title";s:12:"心顺物业";s:4:"pic1";s:24:"/Public/images/top-1.jpg";s:4:"hits";s:1:"0";s:7:"content";s:21:"心顺物业招商中";s:7:"classid";s:1:"1";s:4:"time";s:10:"1401174267";s:3:"tag";s:48:"青果网，清新网站，文字控，技术宅";s:9:"classname";N;}}
