tr>\r\n			<td height=\"20\" class=\"xl29\" width=\"348\">\r\n				名称\r\n			</td>\r\n			<td colspan=\"2\" class=\"xl30\" width=\"436\">\r\n				店主详情\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"22\" class=\"xl24\" width=\"348\">\r\n			</td>\r\n			<td class=\"xl25\" width=\"297\">\r\n				姓名\r\n			</td>\r\n			<td class=\"xl26\" width=\"139\">\r\n				张三\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"22\" class=\"xl24\" width=\"348\">\r\n				小型超市\r\n			</td>\r\n			<td class=\"xl25\" width=\"297\">\r\n				电话\r\n			</td>\r\n			<td class=\"xl26\" width=\"139\">\r\n				15256565656\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"22\" class=\"xl27\" width=\"348\">\r\n			</td>\r\n			<td class=\"xl25\" width=\"297\">\r\n				特色\r\n			</td>\r\n			<td class=\"xl26\" width=\"139\">\r\n				免费\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"20\" class=\"xl27\" width=\"348\">\r\n			</td>\r\n			<td class=\"xl25\" width=\"297\">\r\n				住址\r\n			</td>\r\n			<td class=\"xl26\" width=\"139\">\r\n				山东聊城开发区\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"20\" class=\"xl27\" width=\"348\">\r\n			</td>\r\n			<td class=\"xl25\" width=\"297\">\r\n				店铺位置\r\n			</td>\r\n			<td class=\"xl26\" width=\"139\">\r\n				聊城东昌府区\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"20\" class=\"xl28\" width=\"348\">\r\n			</td>\r\n			<td class=\"xl25\" width=\"297\">\r\n				微信号\r\n			</td>\r\n			<td class=\"xl26\" width=\"139\">\r\n				48484848874<br />\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<br />',null,null,'0',null);/* MySQLReback Separation */
