 INSERT INTO `tg_about` VALUES ('5','企业文化','&nbsp;★我们的企业理念：<br />\r\n■弘扬民族精华！创建心顺品牌！<br />\r\n<br />\r\n★我们的服务理念：<br />\r\n■业主至上！用爱心提供知心、贴心服务！<br />\r\n<br />\r\n★我们的服务宗旨：<br />\r\n■全心全意为业主服务！ <br />\r\n<br />\r\n★我们的服务方针：<br />\r\n■依法管理！诚信为本！科学高效！精益求精！<br />\r\n<br />\r\n★我们的服务目标是：<br />\r\n■用我们的爱心、细心、精心为业主营造一个<br />\r\n顺心如意的工作生活环境！实现物业的保值增值！<br />\r\n<br />\r\n★ 我们的口号：<br />\r\n■以爱为中心！用心做好事！<br />\r\n<br />\r\n★我们的工作作风：<br />\r\n■积极主动！迅速反应！令行禁止！业主满意！积极进取！完善自我<br />',null,null,'0',null),('6','开拓创新','公司提倡以雷锋为榜样、弘<br />\r\n&nbsp;<br />\r\n扬民族精华，宣传真善美、传播<br />\r\n&nbsp;<br />\r\n正能量，以全心全意为业主服务<br />\r\n&nbsp;<br />\r\n为宗旨。<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; 1、第一家在所有物业服务<br />\r\n&nbsp;<br />\r\n项目中设置学雷锋小组并积极实<br />\r\n&nbsp;<br />\r\n施的物业公司<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; 2、第一家在住宅小区物业<br />\r\n&nbsp;<br />\r\n服务项目中设置免租赁费便民商<br />\r\n&nbsp;<br />\r\n铺，给业主生活提供便捷、实惠<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3、第一家在所有物业服务<br />\r\n&nbsp;<br />\r\n项目中设置便捷实用的多渠道与<br />\r\n&nbsp;<br />\r\n业主沟通、交流平台，切实了解<br />\r\n&nbsp;<br />\r\n业主心声，积极提供相应服务<br />\r\n&nbsp; &nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;4、第一家在所有物业服务<br />\r\n&nbsp;<br />\r\n项目中以业主满意度为考核基准<br />\r\n&nbsp;<br />\r\n的物业公司<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5、在内部管理中、实行了<br />\r\n&nbsp;<br />\r\n积极有效的绩效考核，让每位员<br />\r\n&nbsp;<br />\r\n工积极主动做好每项工作<br />',null,null,'0',null);/* MySQLReback Separation */
