 INSERT INTO `tg_about` VALUES ('9','人才招聘','项目经理<br />\r\n&nbsp;<br />\r\n岗位描述&nbsp;<br />\r\n&nbsp;<br />\r\n1、负责对业已制定的各服务部<br />\r\n&nbsp;<br />\r\n门的制度和标准操作程序的管理<br />\r\n&nbsp;<br />\r\n、运作和执行进行监督与协调；<br />\r\n&nbsp;<br />\r\n2、负责服务质量以及客户整体<br />\r\n&nbsp;<br />\r\n满意度、管理支持和部门之间的<br />\r\n&nbsp;<br />\r\n合作。&nbsp;<br />\r\n&nbsp;<br />\r\n3、负责执行并监控公司所提供<br />\r\n&nbsp;<br />\r\n的各项服务计划。<br />\r\n&nbsp;<br />\r\n4、根据公司的规定执行公司<br />\r\n&nbsp;<br />\r\n的各项服务计划。<br />\r\n&nbsp;<br />\r\n5、监控各部门的计划，保证其<br />\r\n&nbsp;<br />\r\n正确执行。<br />\r\n&nbsp;<br />\r\n6、根据需要，为各部门经理安<br />\r\n&nbsp;<br />\r\n排提供质量保证、培训支持、财<br />\r\n&nbsp;<br />\r\n务支持和人力资源。<br />\r\n&nbsp;<br />\r\n7、对项目中所提供的各项服务<br />\r\n&nbsp;<br />\r\n制订年度预算程序，并加以监督<br />\r\n&nbsp;<br />\r\n8、根据计划来制订年度预算，<br />\r\n&nbsp;<br />\r\n并根据预算完成每月工作。<br />\r\n&nbsp;<br />\r\n任职要求：&nbsp;<br />\r\n&nbsp;<br />\r\n1、大专及以上学历；<br />\r\n&nbsp;<br />\r\n2、持有物业管理经理上岗证书；<br />\r\n&nbsp;<br />\r\n3、年龄28～45，身体健康；做<br />\r\n&nbsp;<br />\r\n过医院后勤的优先；&nbsp;<br />\r\n&nbsp;<br />\r\n4、从事物业管理或酒店管理相<br />\r\n&nbsp;<br />\r\n关经验五年以上，医院物业行业<br />\r\n&nbsp;<br />\r\n管理经验三年以上<br />\r\n&nbsp;<br />\r\n5、熟悉物业管理实务运作及法<br />\r\n&nbsp;<br />\r\n律法规、ISO9001及服务质量管<br />\r\n&nbsp;<br />\r\n理；&nbsp;<br />\r\n&nbsp;<br />\r\n6、具有较强的计划、组织、领<br />\r\n&nbsp;<br />\r\n导、协调、沟通及控制能力；&nbsp;<br />\r\n&nbsp;<br />\r\n7、语言表达能力、文字写作处<br />\r\n&nbsp;<br />\r\n理能力强，熟练办公自动化等办<br />\r\n&nbsp;<br />\r\n公软件。&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n区域市场拓展经理<br />\r\n&nbsp;<br />\r\n岗位职责<br />\r\n&nbsp;<br />\r\n1、根据公司市场拓展部门的总<br />\r\n&nbsp;<br />\r\n体发展战略，完成所辖区域销售<br />\r\n&nbsp;<br />\r\n工作目标；&nbsp;<br />\r\n&nbsp;<br />\r\n2、负责所管区域的支持管理服<br />\r\n&nbsp;<br />\r\n务市场的开拓，建立和维护行业<br />\r\n&nbsp;<br />\r\n客户资源，完成年度销售任务；<br />\r\n&nbsp;<br />\r\n3、熟悉所管区域的潜在客户状<br />\r\n&nbsp;<br />\r\n况，进行定期或不定期客户拜访<br />\r\n&nbsp;<br />\r\n，并保持良好关系；<br />\r\n&nbsp;<br />\r\n4、负责研究所管区域的销售情<br />\r\n&nbsp;<br />\r\n况，进行市场调查，收集、归纳<br />\r\n&nbsp;<br />\r\n整理市场信息，进行需求分析；<br />\r\n&nbsp;<br />\r\n5、建立健全客户档案，制订销<br />\r\n&nbsp;<br />\r\n售访问计划，不定期检查销售访<br />\r\n&nbsp;<br />\r\n问效果；<br />\r\n&nbsp;<br />\r\n6、不断熟悉公司运营情况、核<br />\r\n&nbsp;<br />\r\n心业务知识和运作流程，并以最<br />\r\n&nbsp;<br />\r\n佳方式展现给客户<br />\r\n&nbsp;<br />\r\n7、不断改进工作方法和服务程<br />\r\n&nbsp;<br />\r\n序，努力提高销售水平；<br />\r\n&nbsp;<br />\r\n8、负责所辖区域投标项目各项<br />\r\n&nbsp;<br />\r\n情况的追踪、合同细节的确认及<br />\r\n&nbsp;<br />\r\n合同签订等；<br />\r\n&nbsp;<br />\r\n9、协助运作部门对新接项目筹<br />\r\n&nbsp;<br />\r\n备，与其他部门协调沟通，密切<br />\r\n&nbsp;<br />\r\n合作；<br />\r\n&nbsp;<br />\r\n10、负责做好本职业务范围内的<br />\r\n&nbsp;<br />\r\n成本控制工作；<br />\r\n&nbsp;<br />\r\n11、完成公司上级交办的其他工<br />\r\n&nbsp;<br />\r\n作和协调职能范围内的相关事宜<br />\r\n&nbsp;<br />\r\n工作。<br />\r\n&nbsp;<br />\r\n履职要求：<br />\r\n&nbsp;<br />\r\n1、年龄25岁以上，能承受较大<br />\r\n&nbsp;<br />\r\n工作压力；<br />\r\n&nbsp;<br />\r\n2、大专以上学历，有大客户销<br />\r\n&nbsp;<br />\r\n售工作经验优先；<br />\r\n&nbsp;<br />\r\n3、对市场营销工作有深刻认知<br />\r\n&nbsp;<br />\r\n，有成功的销售经验；<br />\r\n&nbsp;<br />\r\n4、具有敏锐的市场感知、把握<br />\r\n&nbsp;<br />\r\n市场动态和市场方向的能力；<br />\r\n&nbsp;<br />\r\n5、高度的工作热情，良好的团<br />\r\n&nbsp;<br />\r\n队合作精神，良好的沟通、协调<br />\r\n&nbsp;<br />\r\n、组织与开拓能力；<br />\r\n&nbsp;<br />\r\n6、较强的观察力和应变能力；<br />\r\n&nbsp;<br />\r\n7、能够适应出差。<br />\r\n●有意向可投递简历至www.xinshunwuye@163.com邮箱，或致电0635--8906857咨询。<br />\r\n●其它招聘职位的详情，请至58同城招聘网站搜索公司名称“心顺物业”了解。<br />',null,null,'0',null);/* MySQLReback Separation */
 /* 插入数据 `tg_about` */
 INSERT INTO `tg_about` VALUES ('11','与开发商合作项目意向明细表','开发商应提供的资料：<br />\r\n<br />\r\n1、营业执照、组织机构代码证、税务登记证、资质证书、法人代表及授权委托人身份证的扫描件 <br />\r\n<br />\r\n2、合作楼盘的建筑工程施工许可证扫描件，总平面图、鸟瞰图的CAD版 <br />\r\n<br />\r\n请开发商将以上证件需扫描后发送到xinshunwuye@163.com邮箱',null,null,'0',null),('12','各地折扣店招商意向表','招商简要：<br />\r\n<br />\r\n1、商店必须是经营中的实体店，经营种类请按网站首页折扣店导航中的栏目填写。 <br />\r\n<br />\r\n2、本网站暂时不收取商店佣金，免费提供服务；为我们的业主提供实惠方便。 <br />\r\n<br />\r\n3、我们需要将商店所在位置添加到网站导航系统，商店需支付400元费用，每更改一次信息需支付10元工本费。 <br />\r\n<br />\r\n4、商店发布的信息内容健康、语言文明、遵规守法，如违犯、将不予发布。 <br />\r\n<br />\r\n5、商店不允许上传动漫、影视等信息，可以添加照片、表格等。图片限制在5张以内，每张最大100k <br />\r\n<br />\r\n6、以上内容如有更改不在另行通知，解释权归心顺物业所有。未注明事项依照国家现行法律法规执行。 <br />\r\n<br />\r\n7、如双方达成合作意向，具体内容另行约定。 <br />\r\n<br />\r\n店主应提供的资料： <br />\r\n<br />\r\n1、营业执照、质量安全证、店主及联系人身份证复印件。 <br />\r\n<br />\r\n请店主将证件扫描后发送到xinshunwuye@163.com邮箱。',null,null,'0',null),('13','全国品牌厂家意向招商明细','需要提供的资质证书及质量证明、法人及授权委托人身份证复印件，授权证书等:<br />\r\n<br />\r\n1、农副产品：（水果、粮油、五谷杂粮等）绿色认证、有机认证、企业营业执照、质量安全证书、食品流通许可证书、组织机构代码证、保险公司担保资料等。 <br />\r\n<br />\r\n2、食品类、(糕点、饮品等)企业营业执照、质量安全证书、食品流通许可证书、组织机构代码证、保险公司担保资料等。 <br />\r\n<br />\r\n3、成品类：（家电、家具、家纺、日常用品等）。应提供营业执照、质量安全证书、	经营许可证、机构代码证、保险公司担保资料等。以上证件需扫描后发送到xinshunwuye@163.com邮箱<br />\r\n<br />\r\n招商简要： <br />\r\n<br />\r\n1、以上产品必须是知名品牌商品、本地特色食品、农副产品等。 <br />\r\n<br />\r\n2、请厂家提供由保险公司担保的相应产品质量及品牌证明。 <br />\r\n<br />\r\n3、不收取厂家信息占用网站空间的佣金。 <br />\r\n<br />\r\n4、根据实际团购数量、金额、品种、收取佣金。 <br />\r\n<br />\r\n5、如实际到货质量于宣传不符，心顺物业有权退货，厂家必须赔偿相应的一切损失。 <br />\r\n<br />\r\n6、厂家必须选择与其商品相匹配的装卸、交通运输用具及保证措施，保质保量的安全送到目的地，以上所产生的费用由厂商负责。 <br />\r\n<br />\r\n7、以上内容如有更改不在另行通知，最终解释权归心顺物业所有。未注明事项依照国家现行法律法规执行。 <br />\r\n<br />\r\n8、如双方达成合作意向，具体内容另行约定。 <br />',null,null,'0',null);/* MySQLReback Separation */
