 INSERT INTO `tg_xw` VALUES ('5','发发发发发发发发发发发发发发发发发发发反反复复','发发发发发发发发发发发发发反反复复方法','0','1400809345','3','青果网，清新网站，文字控，技术宅',null,null,'1','2','0'),('6','生如夏花','作词:朴树<br />\r\n作曲:朴树<br />\r\n编曲:张亚东<br />\r\n也不知在黑暗中究竟沉睡了多久<br />\r\n也不知要有多难才能睁开双眼<br />\r\n我从远方赶来恰巧你们也在<br />\r\n痴迷流连人间我为她而狂野<br />\r\n我是这耀眼的瞬间<br />\r\n是划过天边的刹那火焰<br />\r\n我为你来看我不顾一切<br />\r\n我将熄灭永不能再回来<br />\r\n我在这里啊<br />\r\n就在这里啊<br />\r\n惊鸿一般短暂<br />\r\n像夏花一样绚烂<br />\r\n（ 哦哇啦咿耶，咿哇啦咿呀……）<br />\r\n这是一个多美丽又遗憾的世界<br />\r\n我们就这样抱着笑着还流着泪<br />\r\n我从远方赶来赴你一面之约<br />\r\n痴迷流连人间我为她而狂野<br />\r\n我是这耀眼的瞬间<br />\r\n是划过天边的刹那火焰<br />\r\n我为你来看我不顾一切<br />\r\n我将熄灭永不能再回来<br />\r\n我在这里啊<br />\r\n就在这里啊<br />\r\n惊鸿一般短暂<br />\r\n如夏花一样绚烂<br />\r\n我是这耀眼的瞬间<br />\r\n是划过天边的刹那火焰<br />\r\n我为你来看我不顾一切<br />\r\n我将熄灭永不能再回来<br />\r\n不虚此行呀<br />\r\n不虚此行呀<br />\r\n惊鸿一般短暂<br />\r\n开放在你眼前<br />\r\n我是这耀眼的瞬间<br />\r\n是划过天边的刹那火焰<br />\r\n我要你来爱我不顾一切<br />\r\n我将熄灭永不能再回来<br />\r\n一路春光啊<br />\r\n一路荆棘呀<br />\r\n惊鸿一般短暂<br />\r\n如夏花一样绚烂<br />\r\n这是一个不能停留太久的世界<br />','53','1405867908','2','青果网，清新网站，文字控，技术宅','/Upload/image/201407/20140720145134_95649.jpg',null,'1','0','0'),('7','嘎嘎嘎','古古怪怪','2','1400810284','3','青果网，清新网站，文字控，技术宅',null,null,'1','5','0'),('8','发发发发发发发发发发发发发发','发发发发发发发发发发发发发发发','0','1400807826','3','青果网，清新网站，文字控，技术宅',null,'tt','1','6','0'),('9','sssssss','sssssssssss','2','1403320440','3','青果网，清新网站，文字控，技术宅',null,null,'1','0','0'),('10','这是一个神奇的网站','收拾收拾','0','1401174609','3','青果网，清新网站，文字控，技术宅','/Upload/image/201405/20140524071853_36628.jpg',null,'1','6','0'),('11','发 布 免 费 广 告发 布 免 费 广 告','发 布 免 费 广 告发 布 免 费 广 告发 布 免 费 广 告发 布 免 费 广 告发 布 免 费 广 告发 布 免 费 广 告发 布 免 费 广 告','2','1400918598','3','青果网，清新网站，文字控，技术宅',null,null,'1','7','0'),('12','心顺物业','心顺物业招商中','0','1401174267','1','青果网，清新网站，文字控，技术宅','/Public/images/top-1.jpg',null,'1','0','0'),('13','心顺物业2',null,'0','1401174254','1','青果网，清新网站，文字控，技术宅','/Public/images/top-2.jpg',null,'1','0','0'),('14','心顺物业3',null,'0','1401174246','1','青果网，清新网站，文字控，技术宅','/Public/images/top-1.jpg',null,'1','0','0'),('