INSERT INTO `tg_xw` VALUES ('6', '生如夏花', '作词:朴树<br />\r\n作曲:朴树<br />\r\n编曲:张亚东<br />\r\n也不知在黑暗中究竟沉睡了多久<br />\r\n也不知要有多难才能睁开双眼<br />\r\n我从远方赶来恰巧你们也在<br />\r\n痴迷流连人间我为她而狂野<br />\r\n我是这耀眼的瞬间<br />\r\n是划过天边的刹那火焰<br />\r\n我为你来看我不顾一切<br />\r\n我将熄灭永不能再回来<br />\r\n我在这里啊<br />\r\n就在这里啊<br />\r\n惊鸿一般短暂<br />\r\n像夏花一样绚烂<br />\r\n（ 哦哇啦咿耶，咿哇啦咿呀……）<br />\r\n这是一个多美丽又遗憾的世界<br />\r\n我们就这样抱着笑着还流着泪<br />\r\n我从远方赶来赴你一面之约<br />\r\n痴迷流连人间我为她而狂野<br />\r\n我是这耀眼的瞬间<br />\r\n是划过天边的刹那火焰<br />\r\n我为你来看我不顾一切<br />\r\n我将熄灭永不能再回来<br />\r\n我在这里啊<br />\r\n就在这里啊<br />\r\n惊鸿一般短暂<br />\r\n如夏花一样绚烂<br />\r\n我是这耀眼的瞬间<br />\r\n是划过天边的刹那火焰<br />\r\n我为你来看我不顾一切<br />\r\n我将熄灭永不能再回来<br />\r\n不虚此行呀<br />\r\n不虚此行呀<br />\r\n惊鸿一般短暂<br />\r\n开放在你眼前<br />\r\n我是这耀眼的瞬间<br />\r\n是划过天边的刹那火焰<br />\r\n我要你来爱我不顾一切<br />\r\n我将熄灭永不能再回来<br />\r\n一路春光啊<br />\r\n一路荆棘呀<br />\r\n惊鸿一般短暂<br />\r\n如夏花一样绚烂<br />\r\n这是一个不能停留太久的世界<br />\r\n<embed src=\"http://www.xiami.com/widget/15269577_378727,_235_346_5695c1_457cb4_1/multiPlayer.swf\" type=\"application/x-shockwave-flash\" width=\"235\" height=\"346\" />', '73', '1406346715', '2', '青果网，清新网站，文字控，技术宅', '/Upload/image/201407/20140726115129_34027.jpg', '', '1', '0', '0', '');
